mapper.select:
SELECT client.`name` AS "clientName", client.province AS "province", client.city AS "city", client.area AS "area", client.community AS "community", client.address AS "address", client.longitude AS "longitude", client.latitude AS "latitude", contact.`name` AS "contactName", contact.mobile_phone AS "contactPhone", contact.email AS "email", workOrder.`status` AS "status", workOrder.type AS "type", workOrder.id AS "id", workOrder.number AS "number", detail.assigned_time AS "assignedTime", detail.description AS "description", device.`name` AS "deviceName", device.signing AS "signing" FROM `ic_work_order` workOrder LEFT JOIN tb_client client ON client.id = workOrder.client_id LEFT JOIN tb_contact contact ON contact.id = workOrder.contact_id LEFT JOIN ic_work_order_detail detail ON workOrder.id = detail.work_order_id LEFT JOIN tb_device_number device ON device.id = detail.device_number WHERE 1=1
AND detail.service_engineer_name = #{userName}
AND workOrder.status = #{status}
ORDER BY workOrder.declaration_time DESC

SELECT client.`name` AS "clientName", contact.`name` AS "contactName", contact.mobile_phone AS "contactPhone", contact.email AS "email", client.province AS "province", client.city AS "city", client.area AS "area", client.community AS "community", client.address AS "address", workOrder.`status` AS "status", workOrder.type AS "type", detail.assigned_time AS "assignedTime", workOrder.id AS "id", workOrder.number AS "number" FROM `ic_work_order` workOrder LEFT JOIN tb_client client ON client.id = workOrder.client_id LEFT JOIN tb_contact contact ON contact.client_id = client.id LEFT JOIN ic_work_order_detail detail ON workOrder.id = detail.work_order_id LEFT JOIN tb_wx_user wxUser ON wxUser.client_id = client.id WHERE wxUser.open_id = #{openId}
AND workOrder.`status` IN ${status}
ORDER BY workOrder.declaration_time DESC

SELECT workOrder.* FROM ic_work_order workOrder WHERE 1=1
AND workOrder.client_id = #{clientId}
AND workOrder.`status` = #{status}
AND ( SELECT COUNT(*) FROM ic_service_commentery WHERE work_order_id = workOrder.id ) = 0
